
public class Akshay1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Post Increment
		for (int a = 1;a<=5;a++)
		{
			System.out.println(a + "\n");
			
		}
		
		// Pre Increment
		for (int a = 1;a<=5;++a)
		{
			System.out.println(a + "\n");
			
		}
		
		while (a<=5)
		{
			System.out.println("a");
			
			a++;
		}

	}

}
