
public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		for (a = 1; a <= 5; a++) {
			System.out.println(a + "\n");
		}
		for (a = 1; a <= 5; ++a) {
			System.out.println(a + "\n");
		}

		while (a <= 10) {
			System.out.println(a + "\n");
			a++;
		}
	do {
		System.out.println(a + "\n");
		a++;
	}while(a<=10);
	}
	
}